import { useEffect, useState } from 'react';
import { buscaTask } from './utils';

function Adicionar() {
const [tasks, setTasks] = useState([]);
const [loading, setLoading] = useState(true);

async function getTasks() {
    buscaTask().then((task) => setTasks(task));
    setLoading(false);
}

useEffect(() => {
        getTasks();
      }, []);
  return (
    <div className="App">
        <h1> AAAAAAAAAAAAA</h1>
        <div>
            <p>Descrição:</p>
            <p></p>
        </div>
        {!loading ? tasks.map((task) => { 
            return (
                <div className="tasks" >
                    <p>{task.descricao}</p>
                    <p>{task.status}</p>
                    <p>{task.data}</p>
                    <p>{task.prioridade}</p>
                </div>
            )
        }) : <p>Carregando</p>}
    </div>
  );
}

export default Adicionar;
