import { buscaTask } from './utils';
import { useEffect, useState } from 'react';
import './App.css';

function Adicionar() {
const [tasks, setTasks] = useState([]);
const [loading, setLoading] = useState(true);

function getTasks() {
    buscaTask().then((data) => setTasks[data]);
    setLoading(false);
}

useEffect(() => {
        getTasks();
      }, []);
  return (
    <div className="App">
        <h1> AAAAAAAAAAAAA</h1>
        {!loading ? tasks.map((task) => {
            return (
                <div>
                    <h1>{task.description}</h1>
                    <h1>{task.status}</h1>
                </div>
            )
        }) : <p>Carregando</p>}
    </div>
  );
}

export default Adicionar;
