import { useEffect, useState } from 'react';
import { useTasks } from "./context/myContext";
import { buscaTask, deleteTask } from './utils';

function Tasks() {
const { tasks, setTasks} = useTasks();
const [loading, setLoading] = useState(true);

async function getTasks() {
    buscaTask().then((task) => setTasks(task));
    setLoading(false);
}

async function removeTask(id) {
   const taskId = { id: Number(id) };
   await deleteTask(taskId);
   await buscaTask().then((task) => setTasks(task));
}

useEffect(() => { setTasks(tasks) }, [tasks, setTasks] )

useEffect(() => {
        getTasks();
      }, []);

  return (
    <main className="App">
        <h1> Agenda de tarefas  üïó </h1>
        <tr className="header">
            <td>Numero</td>
            <td>Descri√ß√£o:</td>
            <td>Status:</td>
            <td>Data:</td>
            <td>Prioridade:</td>
        </tr>
        <hr />
        {!loading ? tasks.map((task) => { 
            const data = task.data.substring(8, 10) + task.data.substring(4, 8) + task.data.substring(0, 4);
            return (
                <tr className="tasks" key={ task.id }>
                    <td>{task.id} </td>
                    <td>{task.descricao}</td>
                    <td className="status">{task.status}</td>
                    <td className="data">{data}</td>
                    <td>{task.prioridade}</td>
                    {/* <button className="apagar" onClick={() => removeTask(task.id) } >‚ùå</button> */}
                </tr>
            )
        }) : <p>Carregando</p>}
    </main>
  );
}

export default Tasks;
